import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';

import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import {
    FormGroup,
    Input,
    } from 'reactstrap';
import MatButton from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
import FormLabel from '@material-ui/core/FormLabel';
import FormControl from '@material-ui/core/FormControl';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import Checkbox from '@material-ui/core/Checkbox';
import { Line } from 'react-chartjs-2';
// React Notification
import { toast } from "react-toastify";
import Spinner from 'react-bootstrap/Spinner';

import Paper from '@material-ui/core/Paper';
import Autocomplete from '@material-ui/lab/Autocomplete';
import parse from 'autosuggest-highlight/parse';
import match from 'autosuggest-highlight/match';

import TagFacesIcon from '@material-ui/icons/TagFaces';
import axios from 'axios';  
import {url} from 'axios/url';
import TextField from '@material-ui/core/TextField';

// {/* Auto textfield complete */}



const useStyles = makeStyles(theme => ({
    root2: {
      flexGrow: 1,
      width: '100%',
      backgroundColor: theme.palette.background.paper,
      margin:theme.spacing(7),
      bullet: {
          display: 'inline-block',
          margin: '0 2px',
          transform: 'scale(0.8)',
        },
        title: {
          fontSize: 12,

        },
        pos: {
          fontSize: 11,
        },
      cardContent:{
          padding: 2,
      },
      cardroot:{
          margin:theme.spacing(1),
          height: 250 + 'px !important' ,
      }

      },

      root: {
          '& .MuiTextField-root': {
          margin: theme.spacing(1),
          width: 200,
          },

      },

      formroot: {
          '& .MuiTextField-root': {
          margin: theme.spacing(1),
          width: 200,
          },
      },

      heading: {
          fontSize: theme.typography.pxToRem(15),
      },
      secondaryHeading: {
          fontSize: theme.typography.pxToRem(15),
          color: theme.palette.text.secondary,
      },
      icon: {
          verticalAlign: 'bottom',
          height: 20,
          width: 20,
      },
      details: {
          alignItems: 'center',
      },
      column: {
          flexBasis: '33.33%',
      },
      helper: {
          borderLeft: `2px solid ${theme.palette.divider}`,
          padding: theme.spacing(1, 2),
      },
      link: {
          color: theme.palette.primary.main,
          textDecoration: 'none',
          '&:hover': {
          textDecoration: 'underline',
          },
      },
      inforoot: {
          width: '95%',
          margin: 20,
          backgroundColor: '#eee',
      },
      }));



export default function ConsultationPage(props) {
    const classes = useStyles();

    const [state, setState] = React.useState({
        gilad: true,
        jason: false,
        antoine: false,
    });

    const handleChangecheckbox = name => event => {
        setState({ ...state, [name]: event.target.checked });
      };

      const { gilad, jason, antoine } = state;
      const error = [gilad, jason, antoine].filter(v => v).length !== 2;
      const [anchorEl, setAnchorEl] = React.useState(null);
      const open = Boolean(anchorEl);

      const handleClick = event => {
        setAnchorEl(event.currentTarget);
      };

      const handleClose = () => {
        setAnchorEl(null);
      };


    const {getpatient} =props.getpatientdetails ;

   const PatientID = getpatient.row.patientId;
   const visitId = getpatient.row.id;
   //console.log(getpatient);
    //Save Assign Clinician 
    const [consult, setconsult] = useState({ 
            present_consultation: '', 
            patientId: PatientID, 
            visitId:visitId,
            consultation_notes:'',
            formName: 'CONSULTATION_FORM',
            serviceName: 'GENERAL_SERVICE' 
        }); 
    //    console.log(clinician);
    const [showLoading, setShowLoading] = useState(false);  
    const apiUrl = url+"encounters/GENERAL_SERVICE/CONSULTATION_FORM/"+PatientID;  
    const Saveconsult = (e) => { 
    e.preventDefault();  

    const data = {
            formData :{"allergies":["Penicillin","sulfonamides","atracurium","Chemotherapy drugs"]},
            present_consultation: consult.present_consultation, 
            patientId: PatientID, 
            visitId:visitId,
            consultation_notes: consult.consultation_notes,
            formName: 'CONSULTATION_FORM',
            serviceName: 'GENERAL_SERVICE'
    }; 
    console.log(data);
    axios.post(apiUrl, data)
        .then((result) => {          
            setShowLoading(false);
            props.history.push('/checkedin-patients')
            toast.success(" Successful!");
        }).catch((error) => {
            console.log(error);
        setShowLoading(false)
        setconsult(false);

        }
        ); 
    };

const onChange = (e) => {
    e.persist();     
    setconsult({...consult, [e.target.name]: e.target.value});
    } 

return (

    <Grid container spacing={2}>

                <Grid item xs='12'>
                    <Card className={classes.cardroot}>
                            <CardContent>
                                <Typography className={classes.title} color="primary" gutterBottom>
                                Test Order
                                </Typography>
                                    <Grid container spacing={12}>
                                        <Grid item xs='12'>
                                            <Typography className={classes.pos} color="textSecondary" >
                                            <Autocomplete
                                                id="highlights-demo"
                                                style={{ width: 300 }}
                                                options={top100Films}
                                                getOptionLabel={option => option.title}
                                                renderInput={params => (
                                                    <TextField {...params} label="Lab Test Group" variant="outlined" fullWidth margin="normal" />
                                                )}
                                                renderOption={(option, { inputValue }) => {
                                                    const matches = match(option.title, inputValue);
                                                    const parts = parse(option.title, matches);

                                                    return (
                                                    <div>
                                                        {parts.map((part, index) => (
                                                        <span key={index} style={{ fontWeight: part.highlight ? 700 : 400 }}>
                                                            {part.text}
                                                        </span>
                                                        ))}
                                                    </div>
                                                    );
                                                }}
                                                />   

                                                <br/>
                                                <div className={classes.checkboxroot}>
                                                    <FormControl component="fieldset" className={classes.formControl}>
                                                        <FormLabel component="legend">Assign responsibility</FormLabel>
                                                        <FormGroup>
                                                        <FormControlLabel
                                                            control={<Checkbox checked={gilad} onChange={handleChangecheckbox('gilad')} value="gilad" />}
                                                            label="Gilad Gray"
                                                        />
                                                        <FormControlLabel
                                                            control={<Checkbox checked={jason} onChange={handleChangecheckbox('jason')} value="jason" />}
                                                            label="Jason Killian"
                                                        />
                                                        <FormControlLabel
                                                            control={
                                                            <Checkbox checked={antoine} onChange={handleChangecheckbox('antoine')} value="antoine" />
                                                            }
                                                            label="Antoine Llorca"
                                                        />
                                                        </FormGroup>
                                                        <FormHelperText>Be careful</FormHelperText>
                                                    </FormControl>
                                                    <FormControl required error={error} component="fieldset" className={classes.formControl}>
                                                        <FormLabel component="legend">Pick two</FormLabel>
                                                        <FormGroup>
                                                        <FormControlLabel
                                                            control={<Checkbox checked={gilad} onChange={handleChangecheckbox('gilad')} value="gilad" />}
                                                            label="Gilad Gray"
                                                        />
                                                        <FormControlLabel
                                                            control={<Checkbox checked={jason} onChange={handleChangecheckbox('jason')} value="jason" />}
                                                            label="Jason Killian"
                                                        />
                                                        <FormControlLabel
                                                            control={
                                                            <Checkbox checked={antoine} onChange={handleChangecheckbox('antoine')} value="antoine" />
                                                            }
                                                            label="Antoine Llorca"
                                                        />
                                                        </FormGroup>
                                                        <FormHelperText>You can display an error</FormHelperText>
                                                    </FormControl>
                                                    </div>

                                            </Typography>
                                        </Grid>

                                    </Grid>                               
                            </CardContent>                      
                        </Card>

                </Grid>

            </Grid>

)
}

// Top 100 films as rated by IMDb users. http://www.imdb.com/chart/top
const top100Films = [ 
    { title: 'Psycho', year: 1960 },
    { title: 'The Green Mile', year: 1999 },
    { title: 'The Intouchables', year: 2011 },
    { title: 'Modern Times', year: 1936 },
    { title: 'Raiders of the Lost Ark', year: 1981 },
  ];
