import Page from 'components/Page';
import React from 'react';
import { Link } from 'react-router-dom';
import {
    Button,

    Form,

    Alert
} from 'reactstrap';
import {
    Card,
    CardContent,
}
    from '@material-ui/core';
import { TiWarningOutline } from "react-icons/ti";

import { makeStyles } from '@material-ui/core/styles';
import SearchInput from 'components/SearchBox/SearchInput';
import Title from 'components/Title/CardTitle';
import CheckInSearch from './CheckInList';

const useStyles = makeStyles(theme => ({
    card: {
        margin: theme.spacing(20),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },

}));

const PatientPage = (props) => {
    const classes = useStyles();
    const saveCheckedin = (e) => {  
        console.log('button cliked');
      e.preventDefault();  

      const data = { date_visit_start: checkin.date_visit_start, time_visit_start:checkin.time_visit_start, visit_type_id:checkin.visit_type_id };  
      console.log(data);
      axios.post(apiUrl, data)
          .then((result) => {          
            setShowLoading(false);
            props.history.push('/checkedin-patients')
            toast.success("Patient Checked In was Successful!");
            console.log(result);
          }).catch((error) => {
              console.log(error);
          setShowLoading(false)
          setcheckedin(false);
          // console.log("Error in CreateBook!");
          //toast.error("Something went wrong!");
          }
          ); 
    }; 
    return (
        <Page title="Check In" >
            <Alert color="primary">
                <TiWarningOutline
                    size="30"
                    className=" text-dark"/>  { '  '}
                Note : All Available Patients in the system can be search here
            </Alert>
            <Card className={classes.cardBottom}>
                <CardContent>
                    <Title>
                        <Link to="/checkedin-patients">
                            <Button
                                variant="contained"
                                color="primary" className=" float-right mr-1">
                                Checked In Patients
                            </Button>
                        </Link>
                    <br />
                    </Title>
                    <br/>
                    {/* Search Form Input Field */}
                    <Form>
                        <SearchInput />
                    </Form>
                    <br/>
                    <CheckInSearch />
                </CardContent>
            </Card>
        </Page>
    );
};

export default PatientPage;
