import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';

import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Chip from '@material-ui/core/Chip';
import {

    FormGroup,
    Input,
    } from 'reactstrap';

import MatButton from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
// {/* Auto textfield complete */}



const useStyles = makeStyles(theme => ({
    root2: {
      flexGrow: 1,
      width: '100%',
      backgroundColor: theme.palette.background.paper,
      margin:theme.spacing(7),
      bullet: {
          display: 'inline-block',
          margin: '0 2px',
          transform: 'scale(0.8)',
        },
        title: {
          fontSize: 12,

        },
        pos: {
          fontSize: 11,
        },
      cardContent:{
          padding: 2,
      },
      cardroot:{
          margin:theme.spacing(1),
          height: 250 + 'px !important' ,
      }

      },
      alertmsge:{
          marginTop: theme.spacing(2),
      },
      rootaccordia: {
          width: '100%',
      },
      accordiaheading: {
          fontSize: theme.typography.pxToRem(15),
          fontWeight: theme.typography.fontWeightRegular,
      },
      allergiesroot: {
          display: 'flex',
          justifyContent: 'center',
          flexWrap: 'wrap',
          '& > *': {
          margin: theme.spacing(0.5),
          },
      },
      chiproot: {
          display: 'flex',
          justifyContent: 'center',
          flexWrap: 'wrap',
          padding: theme.spacing(0.5),
      },
      chip: {
          margin: theme.spacing(0.5),
      },
      checkboxroot: {
          display: 'flex',
      },
      formControl: {
          margin: theme.spacing(3),
      },

      root: {
          '& .MuiTextField-root': {
          margin: theme.spacing(1),
          width: 200,
          },

      },

      formroot: {
          '& .MuiTextField-root': {
          margin: theme.spacing(1),
          width: 200,
          },
      },

      heading: {
          fontSize: theme.typography.pxToRem(15),
      },
      secondaryHeading: {
          fontSize: theme.typography.pxToRem(15),
          color: theme.palette.text.secondary,
      },
      icon: {
          verticalAlign: 'bottom',
          height: 20,
          width: 20,
      },
      details: {
          alignItems: 'center',
      },
      column: {
          flexBasis: '33.33%',
      },
      helper: {
          borderLeft: `2px solid ${theme.palette.divider}`,
          padding: theme.spacing(1, 2),
      },
      link: {
          color: theme.palette.primary.main,
          textDecoration: 'none',
          '&:hover': {
          textDecoration: 'underline',
          },
      },
      inforoot: {
          width: '95%',
          margin: 20,
          backgroundColor: '#eee',
      },
      }));



export default function ConsultationPage() {
    const classes = useStyles();

return (

<Grid container spacing={2}>
        <Grid item xs='6'>                    
            <Card className={classes.cardroot} >
                <CardContent>
                    <Typography className={classes.title} color="primary" gutterBottom>
                    Latest Vital Signs
                    </Typography>
                        <Grid container spacing={12}>
                            <Grid item xs='6'>
                                <Typography className={classes.pos} color="textSecondary" >
                                        Pulse : <span style={{fontSize: 'bold'}}>56pm</span>

                                </Typography>
                            </Grid>
                            <Grid item xs='6'>
                                <Typography className={classes.pos} color="textSecondary" > 
                                        Weight: 23kg
                                </Typography>
                            </Grid>
                            <Grid item xs='6'>
                                <Typography className={classes.pos} color="textSecondary">
                                        RR : 56pm
                                </Typography>
                            </Grid>
                            <Grid item xs='6'>
                                <Typography className={classes.pos} color="textSecondary">
                                        Height: 23kg
                                </Typography>
                            </Grid>
                            <Grid item xs='6'>
                                <Typography className={classes.pos} color="textSecondary">
                                        Tempreature : 56pm
                                </Typography>
                            </Grid>
                            <Grid item xs='6'>
                                <Typography className={classes.pos} color="textSecondary">
                                        BMI: 23kg
                                </Typography>
                            </Grid>
                            <Grid item xs='6'>
                                <Typography className={classes.pos} color="textSecondary">
                                        Blood Presure : 56pm
                                </Typography>
                            </Grid>
                            <Grid item xs='6'>
                                <Typography className={classes.pos} color="textSecondary">
                                        BMI Sstatus: 23kg
                                </Typography>
                            </Grid>
                            <Grid item xs='23'>
                                <Typography className={classes.pos} color="textSecondary">
                                        Paulse : 56pm
                                </Typography>
                            </Grid>

                        </Grid>                               
                </CardContent>                      
                </Card>                     
        </Grid>

        <Grid item xs='6'>

            <Card className={classes.cardroot} >
                <CardContent>
                    <Typography className={classes.title} color="primary" gutterBottom>
                    Allergies
                    </Typography>
                        <Grid container spacing={12}>
                            <Grid item xs='12'>
                                <Typography className={classes.pos} color="textSecondary" >
                                <div className={classes.allergiesroot}>
                                   <Chip
                                        label="Pencline"
                                        color="secondary"
                                        variant="outlined"
                                    />
                                    <Chip
                                        label="Nut Shirm"
                                        color="secondary"
                                        variant="outlined"
                                    />
                                    <Chip
                                        label="Deletable secondary"
                                        color="secondary"
                                        variant="outlined"
                                    />

                                    </div>

                                </Typography>
                            </Grid>

                        </Grid>                               
                </CardContent>                      
            </Card>
        </Grid>

        <Grid item xs='6'>
            <Card className={classes.cardroot} style={{ height: '200px'}}>
                    <CardContent>
                        <Typography className={classes.title} color="primary" gutterBottom>
                        Forms
                        </Typography>
                            <Grid container spacing={12}>
                                <Grid item xs='12'>
                                    <Typography className={classes.pos} color="textSecondary" >
                                    <FormGroup>

                                    <Input type="textarea" name="text"  style={{height: '150px' }}/>
                                    </FormGroup>

                                    </Typography>
                                </Grid>

                            </Grid>                               
                    </CardContent>                      
                </Card>
        </Grid>
        <Grid item xs='6'>
        <Card className={classes.cardroot} style={{ height: '200px'}}>
                    <CardContent>
                        <Typography className={classes.title} color="primary" gutterBottom>
                        Forms
                        </Typography>
                            <Grid container spacing={12}>
                                <Grid item xs='12'>
                                    <Typography className={classes.pos} color="textSecondary" >
                                    <FormGroup>

                                    <Input type="textarea" name="text"  style={{height: '150px' }}/>
                                    </FormGroup>

                                    </Typography>
                                </Grid>

                            </Grid>                               
                    </CardContent>                      
                </Card>
        </Grid>

        <Grid item xs={12} spacing={3}>
        <MatButton  
                                type="submit" 
                                variant="contained"
                                color="primary"
                                className={classes.button}
                                startIcon={<SaveIcon />}
                             >
                                Save
                        </MatButton>

        </Grid>
    </Grid>

)
}
