import React, {useState} from 'react';
import MatButton from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import {
    Card,
    CardContent,
}
    from '@material-ui/core';
import { Row, Col } from 'reactstrap';
import Title from 'components/Title/CardTitle';
import SaveIcon from '@material-ui/icons/Save';
import CancelIcon from '@material-ui/icons/Cancel';
import Spinner from 'react-bootstrap/Spinner';
// React Notification
import { toast } from "react-toastify";
import { ToastContainer } from "react-toastify";
//Date Picker
import 'react-widgets/dist/css/react-widgets.css';
import { DateTimePicker } from 'react-widgets';
import Moment from 'moment';
import momentLocalizer from 'react-widgets-moment';
import moment from 'moment';

import axios from 'axios';  
import {url} from 'axios/url';

//Dtate Picker package
Moment.locale('en');
momentLocalizer();

const useStyles = makeStyles(theme => ({
    card: {
        margin: theme.spacing(20),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(3),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
    cardBottom: {
        marginBottom: 20
    },
    Select: {
        height:45,
        width: 350,
    },
    button: {
        margin: theme.spacing(1),
    },

    root: {
        '& > *': {
            margin: theme.spacing(1),
        },
    },
    input: {
        display: 'none',
    },
}));

export default function AddVitalsPage(props) {
    const classes = useStyles();

    //Save Vitals 
        const [vitals, setVitals] = useState({ 
                formName: 'VITALS_SIGN_FORM', 
                patientId: props.patient.patientId, 
                serviceName:'CLINICAL_SERVICE', 
                visitId:props.patient.checkInId, 
                dateRegistration:new Date() 
            }); 
        const [formDataForVitals, setformDataForVitals] = useState({pulse:"", respiratoryRate:"", temperature:"", diastolic:"", systolic:"", bodyWeight:"", height:""}) 
        const [showLoading, setShowLoading] = useState(false);  
        const apiUrl = url+"visits";   

        const SaveVitals = (e) => { 
            console.log('the save button is call'); 
        e.preventDefault();  
        const newDatenow = moment(vitals.dateRegistration).format('DD-MM-YYYY');
        const data = { 
                formName: 'VITALS_SIGN_FORM', 
                patientId:vitals.patientId, 
                serviceName:'CLINICAL_SERVICE' ,
                visitId:vitals.visitId,
                formData:formDataForVitals,
                dateRegistration:newDatenow
        };  
        console.log(data);
        axios.post(apiUrl, data)
            .then((result) => {          
                setShowLoading(false);
                props.history.push('/checkedin-patients')
                toast.success("Patient Checked In was Successful!");
                console.log(result);
            }).catch((error) => {
                console.log(error);
            setShowLoading(false)
            setVitals(false);
            // console.log("Error in CreateBook!");
            //toast.error("Something went wrong!");
            }
            ); 
        };

    const onChangeFormdata = (e) => {
        e.persist();     
        setformDataForVitals({...formDataForVitals, [e.target.name]: e.target.value});
        } 


    return (

            <form className={classes.form} onSubmit={SaveVitals}>
                <ToastContainer autoClose={2000} />
                <Card className={classes.cardBottom}>
                    <CardContent>
                        <Title >New Vitals Signs --- {props.patient.hospitalNumber}
                        </Title>
                        <br/>
                        <Grid container spacing={2}>
                            <Grid item xs={12} sm={6}>

                            <DateTimePicker time={false} name="dateRegistration"  id="dateRegistration"   value={vitals.dateRegistration}   onChange={value1 => setVitals({...vitals, dateRegistration: value1})}
                                defaultValue={new Date()} max={new Date()}
                            />

                            </Grid>
                            <Grid item xs={12} sm={6}>

                                <TextField
                                    autoComplete="pulse"
                                    name="pulse"
                                    variant="outlined"
                                    required
                                    fullWidth
                                    id="pulse"
                                    label="Pulse"
                                    autoFocus
                                    size="small"
                                    helperText="Pulse"
                                    value={formDataForVitals.pulse}
                                    onChange={onChangeFormdata}
                                />

                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    autoComplete="pulse1"
                                    name="pulse1"
                                    variant="outlined"
                                    fullWidth
                                    id="pulse1"
                                    label="Pulse(bpm)"
                                    size="small"
                                    value={formDataForVitals.pulse}
                                    onChange={onChangeFormdata}
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    autoComplete="respiration"
                                    name="respiration"
                                    variant="outlined"

                                    fullWidth
                                    id="respiration(bpm)"
                                    label="Respiratory Rate"
                                    size="small"
                                    value={formDataForVitals.respiratoryRate}
                                    onChange={onChangeFormdata}
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    autoComplete="temperature"
                                    name="temperature"
                                    variant="outlined"
                                    required
                                    fullWidth
                                    id="temperature"
                                    label="Temperature(c)"
                                    size="small"
                                    value={formDataForVitals.temperature}
                                    onChange={onChangeFormdata}
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>  
                                <TextField
                                    autoComplete="bloodpressure"
                                    name="bloodpressure"
                                    variant="outlined"
                                    required
                                    fullWidth
                                    id="bpressure"
                                    label="Blood Pressure(mmHg)"
                                    size="small"
                                    helperText="Diatolic"
                                    value={formDataForVitals.diastolic}
                                    onChange={onChangeFormdata}
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    autoComplete="bloodpressure"
                                    name="systolic"
                                    variant="outlined"
                                    required
                                    fullWidth
                                    id="systolic"
                                    label="Blood Pressure(mmHg)"
                                    size="small"
                                    helperText="Sytolic"
                                    value={formDataForVitals.systolic}
                                    onChange={onChangeFormdata}
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}> 
                                <TextField
                                    autoComplete="bodyWeight"
                                    name="bodyWeight"
                                    variant="outlined"
                                    required
                                    fullWidth
                                    id="bodyWeight"
                                    label="Wight(kg)"
                                    size="small"
                                    value={formDataForVitals.bodyWeight}
                                    onChange={onChangeFormdata}
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    autoComplete="height"
                                    name="height"
                                    variant="outlined"
                                    required
                                    fullWidth
                                    id="height"
                                    label="Height(cm)"
                                    size="small"
                                    value={formDataForVitals.height}
                                    onChange={onChangeFormdata}
                                />
                            </Grid>
                        </Grid>
                        <Row>
                            <Col md={12}>
                            {showLoading && 

                                <Spinner animation="border" role="status">
                                <span className="sr-only">Loading...</span>
                                </Spinner> 
                            } 
                            </Col> 
                        </Row>
                        <br/>
                        <MatButton  
                                type="submit" 
                                variant="contained"
                                color="primary"
                                className={classes.button}
                                startIcon={<SaveIcon />}
                             >
                                Save
                        </MatButton>
                        <MatButton
                            variant="contained"
                            color="default"
                            className={classes.button}
                            startIcon={<CancelIcon />}
                        >
                            Cancel
                        </MatButton>
                    </CardContent>
                </Card>
            </form>
    );
}
