import React, {useState} from 'react';
import MatButton from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import {
    Card,
    CardContent,
}
    from '@material-ui/core';
import {
    Col,
    FormGroup,
    Input,
    Label,
    Row,
  } from 'reactstrap';
import Title from 'components/Title/CardTitle';
import SaveIcon from '@material-ui/icons/Save';
import CancelIcon from '@material-ui/icons/Cancel';
import Spinner from 'react-bootstrap/Spinner';
// React Notification
import { toast } from "react-toastify";
import { ToastContainer } from "react-toastify";
//Date Picker
import 'react-widgets/dist/css/react-widgets.css';
import { DateTimePicker } from 'react-widgets';
import Moment from 'moment';
import momentLocalizer from 'react-widgets-moment';
import moment from 'moment';

import axios from 'axios';  
import {url} from 'axios/url';

//Dtate Picker package
Moment.locale('en');
momentLocalizer();

const useStyles = makeStyles(theme => ({
    card: {
        margin: theme.spacing(20),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(3),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
    cardBottom: {
        marginBottom: 20
    },
    Select: {
        height:45,
        width: 350,
    },
    button: {
        margin: theme.spacing(1),
    },

    root: {
        '& > *': {
            margin: theme.spacing(1),
        },
    },
    input: {
        display: 'none',
    },
}));

export default function AddVitalsPage(props) {
    const classes = useStyles();

    //Save Vitals 
        const [vitals, setVitals] = useState({ 
                formName: 'VITALS_SIGN_FORM', 
                patientId: props.patient.patientId, 
                serviceName:'CLINICAL_SERVICE', 
                visitId:props.patient.checkInId, 
                dateEncounter:new Date() 
            }); 
        const [formDataForVitals, setformDataForVitals] = useState({pulse:"", respiratoryRate:"", temperature:"", diastolic:"", systolic:"", bodyWeight:"", height:""}) 
        const [showLoading, setShowLoading] = useState(false);  
        const apiUrl = url+"encounters";   

        const SaveVitals = (e) => { 
            console.log('the save button is call'); 
        e.preventDefault();  
        const newDatenow = moment(vitals.dateEncounter).format('DD-MM-YYYY');
        const data = { 
                formName: 'VITALS_SIGN_FORM', 
                patientId:vitals.patientId, 
                serviceName:'CLINICAL_SERVICE' ,
                visitId:vitals.visitId,
                formData:formDataForVitals,
                dateEncounter:newDatenow
        };  
        console.log(data);
        axios.post(apiUrl, data)
            .then((result) => {          
                setShowLoading(false);
                props.history.push('/checkedin-patients')
                toast.success("Patient Checked In was Successful!");
                console.log(result);
            }).catch((error) => {
                console.log(error);
            setShowLoading(false)
            setVitals(false);
            // console.log("Error in CreateBook!");
            //toast.error("Something went wrong!");
            }
            ); 
        };

    const onChangeFormdata = (e) => {
        e.persist();     
        setformDataForVitals({...formDataForVitals, [e.target.name]: e.target.value});
        } 


    return (

            <form className={classes.form} onSubmit={SaveVitals}>
                <ToastContainer autoClose={2000} />
                <Card className={classes.cardBottom}>
                    <CardContent>
                        <Title >Assign Clinician  --- {props.patient.hospitalNumber}
                        </Title>
                        <br/>

                        <Row form>

                        <Col md={6}>
                            <FormGroup>
                                <Label for="middleName">Next POC </Label>

                                <Input type="select" name="poc" id="poc" value={patient.poc} onChange={onChange}>
                                    <option value="1">General Clinic </option>
                                    <option value="2">Business</option>
                                    <option value="3">Government</option>
                                </Input>
                            </FormGroup>
                            </Col>

                            <Col md={6}>
                            <FormGroup>
                                <Label for="middleName">Available Clinician</Label>

                                <Input type="select" name="occupationId" id="occupationId" value={patient.occupationId} onChange={onChange}>
                                    <option value="1">Dr. Duadua</option>
                                    <option value="2">Dr. Emeka</option>
                                    <option value="3">Dr. Duadua</option>
                                    <option value="1">Dr. Evans</option>
                                    <option value="2">Dr. Dorcas</option>
                                    <option value="3">Dr. Debora</option>
                                </Input>
                            </FormGroup>
                            </Col>

                        </Row>

                        <Row>
                            <Col md={12}>
                            {showLoading && 

                                <Spinner animation="border" role="status">
                                <span className="sr-only">Loading...</span>
                                </Spinner> 
                            } 
                            </Col> 
                        </Row>
                        <br/>
                        <MatButton  
                                type="submit" 
                                variant="contained"
                                color="primary"
                                className={classes.button}
                                startIcon={<SaveIcon />}
                             >
                                Save
                        </MatButton>
                        <MatButton
                            variant="contained"
                            color="default"
                            className={classes.button}
                            startIcon={<CancelIcon />}
                        >
                            Cancel
                        </MatButton>
                    </CardContent>
                </Card>
            </form>
    );
}
