import React  from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

import {
    CardBody,
    Col,
    Row,
    Form,
    Card,
    FormGroup,
    Label,
    Input

} from 'reactstrap';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import SaveIcon from '@material-ui/icons/Save';
import {url} from 'axios/url';


const useStyles = makeStyles(theme => ({
    root2: {
      flexGrow: 1,
      width: '100%',
      backgroundColor: theme.palette.background.paper,
      margin:theme.spacing(7),
      bullet: {
          display: 'inline-block',
          margin: '0 2px',
          transform: 'scale(0.8)',
        },
        title: {
          fontSize: 12,

        },
        pos: {
          fontSize: 11,
        },
      cardContent:{
          padding: 2,
      },
      cardroot:{
          margin:theme.spacing(1),
          height: 250 + 'px !important' ,
      }

      },
      alertmsge:{
          marginTop: theme.spacing(2),
      },
      rootaccordia: {
          width: '100%',
      },
      accordiaheading: {
          fontSize: theme.typography.pxToRem(15),
          fontWeight: theme.typography.fontWeightRegular,
      },
      allergiesroot: {
          display: 'flex',
          justifyContent: 'center',
          flexWrap: 'wrap',
          '& > *': {
          margin: theme.spacing(0.5),
          },
      },

      checkboxroot: {
          display: 'flex',
      },
      formControl: {
          margin: theme.spacing(3),
      },

      root: {
          '& .MuiTextField-root': {
          margin: theme.spacing(1),
          width: 200,
          },

      },

      formroot: {
          '& .MuiTextField-root': {
          margin: theme.spacing(1),
          width: 200,
          },
      },

      heading: {
          fontSize: theme.typography.pxToRem(15),
      },
      secondaryHeading: {
          fontSize: theme.typography.pxToRem(15),
          color: theme.palette.text.secondary,
      },
      icon: {
          verticalAlign: 'bottom',
          height: 20,
          width: 20,
      },
      details: {
          alignItems: 'center',
      },
      column: {
          flexBasis: '33.33%',
      },
      helper: {
          borderLeft: `2px solid ${theme.palette.divider}`,
          padding: theme.spacing(1, 2),
      },
      link: {
          color: theme.palette.primary.main,
          textDecoration: 'none',
          '&:hover': {
          textDecoration: 'underline',
          },
      },
      inforoot: {
          width: '95%',
          margin: 20,
          backgroundColor: '#eee',
      },
      })); 
    const cardStyle = {
        borbderColor: '#fff',
        marginBottom: 10,
    };


export default function Grouped() {
    const apicountries = url+"encounters/pharmacy/drugs";
    const [countries, setCountries] = React.useState([]);
     //Get countries     
      React.useEffect(() => {
        async function getCharacters() {
          const response = await fetch(apicountries);
          const body = await response.json();
          setCountries(body.map(({ genericName, id }) => ({ label: genericName, value: id })));
           //console.log(body);
        }
        getCharacters();

      }, []);
    const classes = useStyles(); 


  return (
      <Form >
          <Row>
                <Col lg={4} >
                  <Card  style={cardStyle} className=" p-3">
                    <CardBody>
                        <Typography className={classes.title} color="primary" gutterBottom>
                                        Drug Order
                        </Typography>
                                <form className={classes.formroot} noValidate autoComplete="off">

                                        <div>
                                        <Col md={4}>
                                            <FormGroup>
                                                <Input type="select" name="maritalStatusId" id="maritalStatusId" >
                                                    <Label for="maritalStatus">Marital Status</Label>
                                                        {countries.map(({ label, value }) => (
                                                            <option key={value} value={value}>
                                                            {label}
                                                            </option>
                                                        ))}
                                                    </Input>
                                            </FormGroup>  
                                        </Col>

                                            <TextField
                                            disabled
                                            id="outlined-required"
                                            label="Drug Unit"
                                            defaultValue="Drug Unit"
                                            variant="outlined"
                                            size="small"
                                            />
                                            <TextField
                                            id="outlined-password-input"
                                            label="Frequency"
                                            autoComplete="current-password"
                                            variant="outlined"
                                            size="small"
                                            />
                                            <TextField
                                            id="outlined-read-only-input"
                                            label="Start Date"
                                            defaultValue="Start Date"
                                            InputProps={{
                                                readOnly: true,
                                            }}
                                            variant="outlined"
                                            size="small"
                                            />
                                            <TextField
                                            id="outlined-number"
                                            label="Duration"
                                            type="number"
                                            InputLabelProps={{
                                                shrink: true,
                                            }}
                                            variant="outlined"
                                            size="small"
                                            />
                                            <TextField id="outlined-search" label="Duration Unit" type="text" variant="outlined" size="small"/>
                                            <TextField
                                            id="outlined-helperText"
                                            label="Enter Instruction"
                                            defaultValue="Enter Instruction"
                                            variant="outlined"
                                            size="small"
                                            />
                                             <br/>
                                                    <Button
                                                        variant="contained"
                                                        color="primary"
                                                        className={classes.button}

                                                        startIcon={<SaveIcon />}
                                                    >
                                                        Save
                                                    </Button>
                                        </div>
                                    </form> 
                    </CardBody>
                  </Card>
                </Col>
                <Col lg={8} >
                    <Row>
                        <Col lg={12} >
                            <Card  style={cardStyle} >
                                <CardBody>
                                    <Typography className={classes.title} color="primary" gutterBottom>
                                            Previous Order
                                    </Typography>                   
                                </CardBody>
                            </Card>
                        </Col>
                        <br/>
                        <br/>
                        <Col lg={12} >
                            <Card  style={cardStyle} >
                                <CardBody>
                                    <Typography className={classes.title} color="primary" gutterBottom>
                                            Current Order
                                    </Typography>
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>

                </Col>

              </Row>
      </Form>

  );
}
